import { Component, signal } from '@angular/core';
import { CounterComponent } from '../../../shared/cpmponents/counter/counter.component'
import { CommonModule } from '@angular/common';
import { WaveAudioComponent } from '../../../info/components/wave-audio/wave-audio.component'

@Component({
  selector: 'app-about',
  standalone: true,
  imports: [CommonModule, CounterComponent, WaveAudioComponent],
  templateUrl: './about.component.html',
  styleUrl: './about.component.css'
})

export class AboutComponent {
  duration = signal(1000);
  message = signal('Hola');

  changeDuration(event: Event) {
    const input = event.target as HTMLInputElement;
    this.duration.set(input.valueAsNumber)
  }

  changeMessage(event: Event) {
    const input = event.target as HTMLInputElement;
    this.message.set(input.value)
  }
}
<div>
    <h1>About</h1>
    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Reprehenderit sed totam repellat modi ex, aliquid voluptatibus consectetur fugit inventore commodi ducimus nobis? Tenetur laboriosam quibusdam esse! Magnam provident inventore rem voluptatibus. Voluptatum itaque alias, perferendis sequi minus odit odio laudantium quaerat nemo possimus consequatur illum quae natus dolorem provident facere?</p>
    <p>Numero de clientes <app-counter [duration]="duration()" [message]="message()" *ngIf="duration() > 0"></app-counter></p>
</div>




<div>
    <input type="number" [value]="duration()" (change)="changeDuration($event)">
</div>

<div>
    <input type="text" [value]="message()" (change)="changeMessage($event)">
</div>

<div>
    <app-counter [duration]="duration()" [message]="message()" *ngIf="duration() > 0"></app-counter>
</div>
