import { Component, signal } from '@angular/core';
import { CounterComponent } from '@shared/components/counter/counter.component'

import { WaveAudioComponent } from '../../../info/components/wave-audio/wave-audio.component'
import { HighlightDirective } from '@shared/directives/highlight.directive'
import { HeaderComponent } from '@shared/components/header/header.component'

@Component({
  selector: 'app-about',
  standalone: true,
  imports: [CounterComponent, WaveAudioComponent, HighlightDirective, HeaderComponent],
  templateUrl: './about.component.html',
  styleUrl: './about.component.css'
})

export default class AboutComponent {
  duration = signal(1000);
  message = signal('Hola');

  changeDuration(event: Event) {
    const input = event.target as HTMLInputElement;
    this.duration.set(input.valueAsNumber)
  }

  changeMessage(event: Event) {
    const input = event.target as HTMLInputElement;
    this.message.set(input.value)
  }
}
<div class="max-w-lg mx-auto px-4 py-8">
  <h1 class="text-2xl md:text-3xl lg:text-4xl font-bold mb-4">About</h1>
  <p class="text-sm md:text-base mb-4">Lorem ipsum dolor <span class="font-semibold">sit amet consectetur adipisicing elit.</span> Reprehenderit sed totam repellat modi ex, aliquid voluptatibus consectetur fugit inventore commodi ducimus nobis? Tenetur laboriosam quibusdam esse! Magnam provident inventore rem voluptatibus. Voluptatum itaque alias, perferendis sequi minus odit odio laudantium quaerat nemo possimus consequatur illum quae natus dolorem provident facere?</p>
  <p class="text-sm md:text-base mb-4">Número de clientes @if (duration() > 0) {
    <app-counter [duration]="duration()" [message]="message()"></app-counter>
  }</p>
  <div class="mb-8">
    <app-wave-audio audioUrl="assets/audio-prueba.mp3"></app-wave-audio>
  </div>
  <a href="/" class="inline-block bg-blue-500 hover:bg-blue-600 text-white font-semibold px-6 py-3 rounded-lg shadow-md transition duration-300 ease-in-out">Back to Homepage</a>
</div>
